contracts/AgentStore.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "./EIP8004/IdentityRegistry.sol";
import "./EIP8004/ReputationRegistry.sol";
import "./EIP8004/ValidationRegistry.sol";

/**
 * @title AgentStore
 * @notice Agent 商店主合约，集成 EIP-8004 功能
 * @dev 提供完整的 Agent 注册、发现、评价和管理功能
 */
contract AgentStore {
    IdentityRegistry public identityRegistry;
    ReputationRegistry public reputationRegistry;
    ValidationRegistry public validationRegistry;

    // 请求方式枚举
    enum RequestMethod {
        GET,
        POST,
        PUT,
        DELETE
    }

    struct AgentListing {
        uint256 agentId;           // Agent ID
        string name;               // Agent 名称
        string description;        // 简介
        string link;               // 链接
        RequestMethod method;       // 请求方式
        string requestParams;      // 请求参数（JSON 字符串）
        uint256 price;             // 价格（以 wei 为单位）
        address owner;             // 所有者
        bool listed;               // 是否上架
        uint256 listedAt;          // 上架时间
        uint256 usageCount;        // 使用次数
    }

    // Agent ID => Listing
    mapping(uint256 => AgentListing) public listings;


    // 所有上架的 Agent IDs
    uint256[] public allListedAgents;

    // 搜索关键词 => Agent IDs（简化版，实际可用链下索引）
    mapping(string => uint256[]) public searchIndex;

    // 事件
    event AgentListed(
        uint256 indexed agentId,
        string name,
        address indexed owner,
        uint256 price,
        uint256 timestamp
    );

    event AgentUnlisted(
        uint256 indexed agentId,
        address indexed owner
    );

    event AgentUsed(
        uint256 indexed agentId,
        address indexed user,
        uint256 timestamp
    );

    constructor(
        address _identityRegistry,
        address _reputationRegistry,
        address _validationRegistry
    ) {
        identityRegistry = IdentityRegistry(_identityRegistry);
        reputationRegistry = ReputationRegistry(_reputationRegistry);
        validationRegistry = ValidationRegistry(_validationRegistry);
    }

    /**
     * @notice 注册并上架 Agent
     * @param name Agent 名称
     * @param description 简介
     * @param link 链接
     * @param method 请求方式
     * @param requestParams 请求参数（JSON 字符串）
     * @param price 价格（以 wei 为单位）
     * @return agentId 新创建的 Agent ID
     */
    function registerAndListAgent(
        string memory name,
        string memory description,
        string memory link,
        RequestMethod method,
        string memory requestParams,
        uint256 price
    ) public returns (uint256 agentId) {
        // 注册身份（使用链接作为 metadataURI）
        agentId = identityRegistry.registerAgent(link);

        // 创建上架信息
        listings[agentId] = AgentListing({
            agentId: agentId,
            name: name,
            description: description,
            link: link,
            method: method,
            requestParams: requestParams,
            price: price,
            owner: msg.sender,
            listed: true,
            listedAt: block.timestamp,
            usageCount: 0
        });

        allListedAgents.push(agentId);

        emit AgentListed(agentId, name, msg.sender, price, block.timestamp);

        return agentId;
    }

    /**
     * @notice 更新 Agent 信息
     * @param agentId Agent ID
     * @param name Agent 名称
     * @param description 简介
     * @param link 链接
     * @param method 请求方式
     * @param requestParams 请求参数
     * @param price 价格
     */
    function updateAgent(
        uint256 agentId,
        string memory name,
        string memory description,
        string memory link,
        RequestMethod method,
        string memory requestParams,
        uint256 price
    ) public {
        require(listings[agentId].owner == msg.sender, "AgentStore: Not owner");
        require(listings[agentId].listed, "AgentStore: Not listed");

        listings[agentId].name = name;
        listings[agentId].description = description;
        listings[agentId].link = link;
        listings[agentId].method = method;
        listings[agentId].requestParams = requestParams;
        listings[agentId].price = price;
    }

    /**
     * @notice 下架 Agent
     * @param agentId Agent ID
     */
    function unlistAgent(uint256 agentId) public {
        require(listings[agentId].owner == msg.sender, "AgentStore: Not owner");
        require(listings[agentId].listed, "AgentStore: Not listed");

        listings[agentId].listed = false;

        emit AgentUnlisted(agentId, msg.sender);
    }

    /**
     * @notice 记录 Agent 使用
     * @param agentId Agent ID
     */
    function recordUsage(uint256 agentId) public {
        require(listings[agentId].listed, "AgentStore: Agent not listed");
        listings[agentId].usageCount += 1;
        emit AgentUsed(agentId, msg.sender, block.timestamp);
    }

    /**
     * @notice 提交评价
     * @param agentId Agent ID
     * @param rating 评分 (1-5)
     * @param comment 评价内容
     */
    function submitRating(
        uint256 agentId,
        uint8 rating,
        string memory comment
    ) public {
        reputationRegistry.submitFeedback(agentId, rating, comment);
    }

    /**
     * @notice 获取所有上架的 Agents
     * @return agentIds Agent ID 数组
     */
    function getAllListedAgents() public view returns (uint256[] memory agentIds) {
        return allListedAgents;
    }

    /**
     * @notice 调用 Agent（支付费用并记录使用）
     * @param agentId Agent ID
     */
    function callAgent(uint256 agentId) public payable {
        AgentListing storage listing = listings[agentId];
        require(listing.listed, "AgentStore: Agent not listed");
        require(msg.value >= listing.price, "AgentStore: Insufficient payment");

        // 如果支付超过价格，退还多余部分
        if (msg.value > listing.price) {
            payable(msg.sender).transfer(msg.value - listing.price);
        }

        // 将费用转给所有者
        payable(listing.owner).transfer(listing.price);

        // 记录使用
        listing.usageCount += 1;

        emit AgentUsed(agentId, msg.sender, block.timestamp);
    }

    /**
     * @notice 获取 Agent 完整信息
     * @param agentId Agent ID
     * @return listing 上架信息
     * @return identity 身份信息
     * @return averageRating 平均评分（放大 1000 倍）
     * @return feedbackCount 反馈数量
     */
    function getAgentFullInfo(uint256 agentId) public view returns (
        AgentListing memory listing,
        IdentityRegistry.AgentIdentity memory identity,
        uint256 averageRating,
        uint256 feedbackCount
    ) {
        listing = listings[agentId];
        identity = identityRegistry.getAgentIdentity(agentId);
        (, uint256 avgRating, uint256 count) = reputationRegistry.getReputation(agentId);
        averageRating = avgRating;
        feedbackCount = count;
    }
}

contracts/EIP8004/IdentityRegistry.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

/**
 * @title IdentityRegistry
 * @notice EIP-8004 身份注册表
 * @dev 为每个 Agent 提供唯一的链上身份标识
 */
contract IdentityRegistry {
    struct AgentIdentity {
        address owner;           // Agent 所有者地址
        string metadataURI;      // IPFS 元数据 URI
        uint256 createdAt;        // 创建时间戳
        bool active;             // 是否激活
    }

    // Agent ID => Agent Identity
    mapping(uint256 => AgentIdentity) public identities;

    // Owner => Agent IDs
    mapping(address => uint256[]) public ownerAgents;

    // 下一个 Agent ID
    uint256 private nextAgentId = 1;

    // 事件
    event AgentRegistered(
        uint256 indexed agentId,
        address indexed owner,
        string metadataURI,
        uint256 timestamp
    );

    event AgentUpdated(
        uint256 indexed agentId,
        address indexed owner,
        string newMetadataURI
    );

    event AgentDeactivated(
        uint256 indexed agentId,
        address indexed owner
    );

    /**
     * @notice 注册新的 Agent 身份
     * @param metadataURI IPFS 元数据 URI
     * @return agentId 新创建的 Agent ID
     */
    function registerAgent(string memory metadataURI) public returns (uint256 agentId) {
        agentId = nextAgentId++;

        identities[agentId] = AgentIdentity({
            owner: msg.sender,
            metadataURI: metadataURI,
            createdAt: block.timestamp,
            active: true
        });

        ownerAgents[msg.sender].push(agentId);

        emit AgentRegistered(agentId, msg.sender, metadataURI, block.timestamp);

        return agentId;
    }

    /**
     * @notice 更新 Agent 元数据
     * @param agentId Agent ID
     * @param newMetadataURI 新的元数据 URI
     */
    function updateAgentMetadata(uint256 agentId, string memory newMetadataURI) public {
        require(identities[agentId].owner == msg.sender, "IdentityRegistry: Not owner");
        require(identities[agentId].active, "IdentityRegistry: Agent inactive");

        identities[agentId].metadataURI = newMetadataURI;

        emit AgentUpdated(agentId, msg.sender, newMetadataURI);
    }

    /**
     * @notice 停用 Agent
     * @param agentId Agent ID
     */
    function deactivateAgent(uint256 agentId) public {
        require(identities[agentId].owner == msg.sender, "IdentityRegistry: Not owner");
        require(identities[agentId].active, "IdentityRegistry: Already inactive");

        identities[agentId].active = false;

        emit AgentDeactivated(agentId, msg.sender);
    }

    /**
     * @notice 获取 Agent 身份信息
     * @param agentId Agent ID
     * @return identity Agent 身份结构
     */
    function getAgentIdentity(uint256 agentId) public view returns (AgentIdentity memory identity) {
        return identities[agentId];
    }

    /**
     * @notice 获取所有者拥有的所有 Agent IDs
     * @param owner 所有者地址
     * @return agentIds Agent ID 数组
     */
    function getOwnerAgents(address owner) public view returns (uint256[] memory agentIds) {
        return ownerAgents[owner];
    }

    /**
     * @notice 检查 Agent 是否存在且激活
     * @param agentId Agent ID
     * @return exists 是否存在
     */
    function agentExists(uint256 agentId) public view returns (bool exists) {
        return identities[agentId].owner != address(0) && identities[agentId].active;
    }
}

contracts/EIP8004/ReputationRegistry.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

/**
 * @title ReputationRegistry
 * @notice EIP-8004 声誉注册表
 * @dev 管理 Agent 的声誉、评分和反馈
 */
contract ReputationRegistry {
    struct Feedback {
        address reviewer;        // 评价者地址
        uint8 rating;            // 评分 (1-5)
        string comment;          // 评价内容
        uint256 timestamp;       // 评价时间
    }

    struct ReputationScore {
        uint256 totalRatings;    // 总评价数
        uint256 sumRatings;      // 评分总和
        uint256 averageRating;   // 平均评分（以 1000 为单位，例如 3500 = 3.5）
        uint256 feedbackCount;   // 反馈数量
    }

    // Agent ID => Reputation Score
    mapping(uint256 => ReputationScore) public reputations;

    // Agent ID => Feedback[]
    mapping(uint256 => Feedback[]) public feedbacks;

    // Agent ID => Reviewer => 是否已评价
    mapping(uint256 => mapping(address => bool)) public hasReviewed;

    // 事件
    event FeedbackSubmitted(
        uint256 indexed agentId,
        address indexed reviewer,
        uint8 rating,
        string comment,
        uint256 timestamp
    );

    /**
     * @notice 提交对 Agent 的反馈
     * @param agentId Agent ID
     * @param rating 评分 (1-5)
     * @param comment 评价内容
     */
    function submitFeedback(
        uint256 agentId,
        uint8 rating,
        string memory comment
    ) public {
        require(rating >= 1 && rating <= 5, "ReputationRegistry: Invalid rating");
        require(!hasReviewed[agentId][msg.sender], "ReputationRegistry: Already reviewed");

        ReputationScore storage reputation = reputations[agentId];

        // 更新声誉评分
        reputation.totalRatings += 1;
        reputation.sumRatings += rating;
        reputation.averageRating = (reputation.sumRatings * 1000) / reputation.totalRatings;
        reputation.feedbackCount += 1;

        // 添加反馈
        feedbacks[agentId].push(Feedback({
            reviewer: msg.sender,
            rating: rating,
            comment: comment,
            timestamp: block.timestamp
        }));

        hasReviewed[agentId][msg.sender] = true;

        emit FeedbackSubmitted(agentId, msg.sender, rating, comment, block.timestamp);
    }

    /**
     * @notice 获取 Agent 的声誉评分
     * @param agentId Agent ID
     * @return totalRatings 总评价数
     * @return averageRating 平均评分（放大 1000 倍）
     * @return feedbackCount 反馈数量
     */
    function getReputation(uint256 agentId) public view returns (
        uint256 totalRatings,
        uint256 averageRating,
        uint256 feedbackCount
    ) {
        ReputationScore memory reputation = reputations[agentId];
        return (
            reputation.totalRatings,
            reputation.averageRating,
            reputation.feedbackCount
        );
    }

    /**
     * @notice 获取 Agent 的所有反馈
     * @param agentId Agent ID
     * @return feedbacksArray 反馈数组
     */
    function getFeedbacks(uint256 agentId) public view returns (Feedback[] memory feedbacksArray) {
        return feedbacks[agentId];
    }

    /**
     * @notice 获取 Agent 的平均评分（1-5）
     * @param agentId Agent ID
     * @return rating 平均评分（浮点数表示，例如 3500 = 3.5）
     */
    function getAverageRating(uint256 agentId) public view returns (uint256 rating) {
        return reputations[agentId].averageRating;
    }
}

contracts/EIP8004/ValidationRegistry.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

/**
 * @title ValidationRegistry
 * @notice EIP-8004 验证注册表
 * @dev 管理 Agent 的验证、证明和争议
 */
contract ValidationRegistry {
    enum ValidationStatus {
        Pending,    // 待验证
        Approved,   // 已通过
        Rejected,   // 已拒绝
        Disputed    // 有争议
    }

    struct Validation {
        uint256 agentId;             // Agent ID
        address validator;           // 验证者地址
        ValidationStatus status;     // 验证状态
        string proofURI;             // 证明 URI（IPFS）
        string notes;                // 验证备注
        uint256 timestamp;           // 验证时间
    }

    struct Dispute {
        uint256 validationId;        // 验证 ID
        address disputer;            // 争议发起者
        string reason;               // 争议原因
        uint256 timestamp;            // 争议时间
        bool resolved;               // 是否已解决
    }

    // Validation ID => Validation
    mapping(uint256 => Validation) public validations;

    // Agent ID => Validation IDs
    mapping(uint256 => uint256[]) public agentValidations;

    // Validation ID => Dispute
    mapping(uint256 => Dispute) public disputes;

    uint256 private nextValidationId = 1;

    // 事件
    event ValidationSubmitted(
        uint256 indexed validationId,
        uint256 indexed agentId,
        address indexed validator,
        ValidationStatus status,
        string proofURI,
        uint256 timestamp
    );

    event ValidationUpdated(
        uint256 indexed validationId,
        ValidationStatus newStatus
    );

    event DisputeRaised(
        uint256 indexed validationId,
        address indexed disputer,
        string reason
    );

    /**
     * @notice 提交 Agent 验证
     * @param agentId Agent ID
     * @param status 验证状态
     * @param proofURI 证明 URI
     * @param notes 验证备注
     * @return validationId 验证 ID
     */
    function submitValidation(
        uint256 agentId,
        ValidationStatus status,
        string memory proofURI,
        string memory notes
    ) public returns (uint256 validationId) {
        validationId = nextValidationId++;

        validations[validationId] = Validation({
            agentId: agentId,
            validator: msg.sender,
            status: status,
            proofURI: proofURI,
            notes: notes,
            timestamp: block.timestamp
        });

        agentValidations[agentId].push(validationId);

        emit ValidationSubmitted(validationId, agentId, msg.sender, status, proofURI, block.timestamp);

        return validationId;
    }

    /**
     * @notice 更新验证状态
     * @param validationId 验证 ID
     * @param newStatus 新状态
     */
    function updateValidationStatus(uint256 validationId, ValidationStatus newStatus) public {
        Validation storage validation = validations[validationId];
        require(validation.validator == msg.sender, "ValidationRegistry: Not validator");

        validation.status = newStatus;

        emit ValidationUpdated(validationId, newStatus);
    }

    /**
     * @notice 对验证提出争议
     * @param validationId 验证 ID
     * @param reason 争议原因
     */
    function raiseDispute(uint256 validationId, string memory reason) public {
        require(validations[validationId].timestamp > 0, "ValidationRegistry: Validation not found");
        require(disputes[validationId].disputer == address(0), "ValidationRegistry: Dispute exists");

        disputes[validationId] = Dispute({
            validationId: validationId,
            disputer: msg.sender,
            reason: reason,
            timestamp: block.timestamp,
            resolved: false
        });

        validations[validationId].status = ValidationStatus.Disputed;

        emit DisputeRaised(validationId, msg.sender, reason);
    }

    /**
     * @notice 解决争议
     * @param validationId 验证 ID
     * @param finalStatus 最终状态
     */
    function resolveDispute(uint256 validationId, ValidationStatus finalStatus) public {
        Dispute storage dispute = disputes[validationId];
        require(dispute.disputer != address(0), "ValidationRegistry: No dispute");
        require(!dispute.resolved, "ValidationRegistry: Already resolved");
        // 注意：实际应用中应该只有仲裁者可以解决争议

        dispute.resolved = true;
        validations[validationId].status = finalStatus;

        emit ValidationUpdated(validationId, finalStatus);
    }

    /**
     * @notice 获取 Agent 的所有验证
     * @param agentId Agent ID
     * @return validationIds 验证 ID 数组
     */
    function getAgentValidations(uint256 agentId) public view returns (uint256[] memory validationIds) {
        return agentValidations[agentId];
    }

    /**
     * @notice 获取验证详情
     * @param validationId 验证 ID
     * @return validation 验证结构
     */
    function getValidation(uint256 validationId) public view returns (Validation memory validation) {
        return validations[validationId];
    }
}
